/**
 * 比较安全的JS 加减乘除
 * @module util.arithmetic
 */

var Decimal = require('decimal.js');

exports.add = function (a, b) {
    a = new Decimal(a);
    b = new Decimal(b);
    return +a.plus(b);
};

exports.minus = function (a, b) {
    a = new Decimal(a);
    b = new Decimal(b);
    return +a.minus(b);
};

exports.multiply = function (a, b) {
    a = new Decimal(a);
    b = new Decimal(b);
    return +a.times(b);
};

exports.divide = function (a, b) {
    a = new Decimal(a);
    b = new Decimal(b);
    return +a.div(b);
}
